mapper.select:



        and u.user_name = #{userName}


        and u.status = #{status}



group by u.id


where
u.id = #{id}

    and u.status = #{status}

group by u.id




        and u.openId = #{openId}


        and u.status = #{status}


        and u.store_id = #{storeId}


group by u.id

select u.id, u.user_name as userName, u.password, u.name, u.sex, u.phone, u.id_card as idCard, u.openId, u.store_id storeId, u.merchants_id merchantsId, u.status, u.remark, u.create_time as createTime, u.update_time as updateTime, u.job, u.uuid, u.head_image headImage, u.introduce from user u
and u.status = #{status}
and u.store_id = #{storeId}
group by u.id

select u.id, u.user_name as userName, u.password, u.name, u.sex, u.phone, u.id_card as idCard, u.openId, u.store_id storeId, u.merchants_id merchantsId, u.status, u.remark, u.create_time as createTime, u.update_time as updateTime, u.job, u.uuid from `user` u
and u.status = #{status}
and u.store_id = #{storeId}
